package Loop;

public class AdditionalSeries3 {
	
	public static void show()
	{
		int num=1;
		System.out.print(num+", ");
		for(int i=1;i<10;i++)
		{
			num=num+i;
			System.out.print(num+" ,");
		}
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		show();

	}

}
